mapper.select:
SELECT
ID,NAME,SN,DESCRIPTION,TO_CHAR(CREATETIME,'yyyy-MM-dd HH24:mi:ss') CREATETIME
FROM (SELECT ROWNUM RN, T.* FROM (

SELECT * FROM BASE_ROLE
WHERE 1 = 1


) T WHERE 1=1
) WHERE 1=1

	AND RN <= #{end}


	AND RN >= #{start}

ORDER BY CREATETIME DESC

SELECT T.ID,T.NAME, T2.CHECKED FROM BASE_ROLE T LEFT JOIN (SELECT DISTINCT ROLE ,
'true'
''
CHECKED FROM BASE_ROLE_USER RU WHERE 1 = 1
AND RU.USERID=#{userId}
) T2 ON T.ID = T2.ROLE

mapper.update:
UPDATE BASE_ROLE
SET
NAME=#{name},
SN=#{sn},
DESCRIPTION=#{description}
WHERE ID = #{id}
